import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import Paper from '@material-ui/core/Paper';
import Zoom from '@material-ui/core/Zoom';
import Button from '@material-ui/core/Button';
import { Link as RouterLink, withRouter } from 'react-router-dom';
import {
   
    Link,
   
} from '@material-ui/core';
 
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Switch from '@material-ui/core/Switch';
import LentilleOrders from '../OrderManagement/components/LentilleOrders'
import ProduitOrders from '../OrderManagement/components/ProduitOrders'
import  WaitingValidation  from './WaitingValidation';
import ValidOrders from './ValidOrders'
import RejectedOrders from './RejectedOrders'
import Price from './Price'
import { Grid } from '@material-ui/core';
import Price1 from './Prices1';
import AddOrder from '../OrderManagement/components/AddOrder';

const useStyles = makeStyles(theme => ({
  appBar: {
    position: 'relative',
  },
  container: {
    display: 'flex',
  },
  root: {
    padding: theme.spacing(4)
  },
  layout: {
    width: 'auto',
    marginLeft: theme.spacing(2),
    marginRight: theme.spacing(2),
    [theme.breakpoints.up(600 + theme.spacing(2) * 2)]: {
      width: 1000,
      marginLeft: 'auto',
      marginRight: 'auto',
    },
  },
  paper: {
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(1),
    padding: theme.spacing(2),
    [theme.breakpoints.up(500 + theme.spacing(3) * 2)]: {
      marginTop: theme.spacing(1),
      marginBottom: theme.spacing(1),
      padding: theme.spacing(2),
    },
  },
 
  buttons: {
    display: 'flex',
     
  },
  button: {
    marginTop: theme.spacing(1),
    marginLeft: theme.spacing(1),
  },
  
 
   
}));



 

export default function GeneralInformation() {
  const classes = useStyles();
  const [checked, setChecked] = React.useState(true);

  const handleChange = () => {
    setChecked(prev => !prev);
  };
  const [checked2, setChecked2] = React.useState(true);

  const handleChange2 = () => {
    setChecked2(prev => !prev);
  };
   
  return (
    <React.Fragment>
      
      <CssBaseline />
      
      <div className={classes.root}>
      <Grid
        container
        spacing={3}
      >
        <Grid
          item
          lg={2}
          sm={4}
          xl={3}
          xs={6}
        >
          <WaitingValidation />
        </Grid>
        <Grid
          item
          lg={2}
          sm={4}
          xl={3}
          xs={6}
        >
          <ValidOrders />
        </Grid>
        <Grid
          item
          lg={2}
          sm={4}
          xl={3}
          xs={12}
        >
          <RejectedOrders />
        </Grid>
        <Grid
          item
          lg={3}
          sm={4}
          xl={3}
          xs={6}
        >
          <Price1 />
        </Grid> 
        
        <Grid
          item
          lg={3}
          sm={4}
          xl={3}
          xs={6}
        >
          <Price />
        </Grid>
       
      </Grid>
    </div>
      <main className={classes.layout}>
        <Paper className={classes.paper}>
           
          <React.Fragment>
            
              <React.Fragment>
             
                <div className={classes.buttons}>
                <Button
                    component={RouterLink}
                    to="/AddOrder"
                    variant="outlined"
                    color="secondary"

                    className={classes.button}
                  >
                   Commander
                  </Button>
                   
                  <FormControlLabel
                   className={classes.button}
                   
          control={
            
            <Switch checked={checked} onChange={handleChange} />
          }
          label=" Produit Orders"
        />
        <FormControlLabel
                   className={classes.button}
                   
          control={
            
            <Switch checked={checked2} onChange={handleChange2} />
          }
          label=" Lentille Orders"
        />
                </div>
               

              </React.Fragment>
            
          </React.Fragment>
        </Paper>
      
      </main>
      <div >
      <div className={classes.container}>
       <Zoom in={checked}>
          <div elevation={20} className={classes.paper}>
            
        <ProduitOrders></ProduitOrders>
          </div>
        </Zoom>
        <Zoom in={checked2}>
          <div elevation={20} className={classes.paper}>
          <LentilleOrders></LentilleOrders>
      
          </div>
        </Zoom>
        
        </div>
        </div>

    </React.Fragment>
  );
}
