import React, { Component } from 'react';
import MaterialTable from 'material-table';
 
import { forwardRef } from 'react';
 
import AddBox from '@material-ui/icons/AddBox';
import ArrowDownward from '@material-ui/icons/ArrowDownward';
import Check from '@material-ui/icons/Check';
import ChevronLeft from '@material-ui/icons/ChevronLeft';
import ChevronRight from '@material-ui/icons/ChevronRight';
import Clear from '@material-ui/icons/Clear';
import DeleteOutline from '@material-ui/icons/DeleteOutline';
import Edit from '@material-ui/icons/Edit';
import FilterList from '@material-ui/icons/FilterList';
import FirstPage from '@material-ui/icons/FirstPage';
import LastPage from '@material-ui/icons/LastPage';
import Remove from '@material-ui/icons/Remove';
import SaveAlt from '@material-ui/icons/SaveAlt';
import Search from '@material-ui/icons/Search';
import ViewColumn from '@material-ui/icons/ViewColumn';
 import Save from '@material-ui/icons/Save';
 import Done from '@material-ui/icons/DoneOutline';
const tableIcons = {
    Add: forwardRef((props, ref) => <AddBox {...props} ref={ref} />),
    Check: forwardRef((props, ref) => <Check {...props} ref={ref} />),
    Clear: forwardRef((props, ref) => <Clear {...props} ref={ref} />),
    Delete: forwardRef((props, ref) => <DeleteOutline {...props} ref={ref} />),
    DetailPanel: forwardRef((props, ref) => <ChevronRight {...props} ref={ref} />),
    Edit: forwardRef((props, ref) => <Edit {...props} ref={ref} />),
    Export: forwardRef((props, ref) => <SaveAlt {...props} ref={ref} />),
    Filter: forwardRef((props, ref) => <FilterList {...props} ref={ref} />),
    FirstPage: forwardRef((props, ref) => <FirstPage {...props} ref={ref} />),
    LastPage: forwardRef((props, ref) => <LastPage {...props} ref={ref} />),
    NextPage: forwardRef((props, ref) => <ChevronRight {...props} ref={ref} />),
    PreviousPage: forwardRef((props, ref) => <ChevronLeft {...props} ref={ref} />),
    ResetSearch: forwardRef((props, ref) => <Clear {...props} ref={ref} />),
    Search: forwardRef((props, ref) => <Search {...props} ref={ref} />),
    SortArrow: forwardRef((props, ref) => <ArrowDownward {...props} ref={ref} />),
    ThirdStateCheck: forwardRef((props, ref) => <Remove {...props} ref={ref} />),
    ViewColumn: forwardRef((props, ref) => <ViewColumn {...props} ref={ref} />),
    Save: forwardRef((props, ref) => <Save {...props} ref={ref} />)
  };
  
 class MyOrders extends Component {
state={
  columns: [
    { title: 'Order no', field: 'Orderno' },
    { title: 'Order date', field: 'Orderdate' , type :"date"},
    { title: 'Category', field: 'Category' ,lookup:  {Produit:'Produit',Lentille:'Lentille'} },
    {
      title: 'Products',
      field: 'Products',
      lookup:{Releasy:'Releasy',SkySoft:'SkySoft'}
    },
    {
      title: 'Status',
      field: 'Status',
      cellStyle: {
        backgroundColor:('green') ,
    }},
    {
      title: 'Comment',
      field: 'Comment',
      
    },
    { title: 'Quantity', field: 'qte', type: 'numeric' },


    { title: 'Puissance', field: 'pss', lookup:  {
      1: '-1,00',  125: '-1,25',15: '-1,50',175: '-1,75',2:'-2,00',225:'-2,25',275:'-2,75',3:'-3,00',325:'-3,25',35:'-3,50',375:'-3,75',4:'-4,00',425:'-4,25',45:'-4,50',475:'-4,75',5:'-5,00',525:'-5,25',55:'-5,50',575:'-5,75',6:'-6,00'}},
  ],
  data: [
    { Orderno: 'Orderno', Orderdate: '18.02.2020', category: 'Produit', Products: 'Releasy',Status:'Status',Comment:'Comment',qte:1,pss:1},
    { Orderno: 'Orderno', Orderdate: '18/02/2020', category: 'Produit', Products: 'Releasy',Status:'Status',Comment:'Comment' ,qte:1,pss:2},
    { Orderno: 'Orderno', Orderdate: '18/02/2020', category: 'Lentille', Products: 'SkySoft',Status:'Status',Comment:'Comment' ,qte:1,pss:225},
    { Orderno: 'Orderno', Orderdate: '18/02/2020', category: 'Lentille', Products: 'SkySoft',Status:'Status',Comment:'Comment',qte:1 ,pss:4},
    { Orderno: 'Orderno', Orderdate: '18/02/2020', category: 'Lentille', Products: 'Releasy',Status:'Status',Comment:'Comment',qte:1 ,pss:5},
    { Orderno: 'Orderno', Orderdate: '18/02/2020', category:  'Produit', Products: 'Releasy',Status:'Status',Comment:'Comment' ,qte:1,pss:4},
    
  ],
  selectedRow: null

}
componentDidMount(){
  this.getOrders();

}
getOrders= _ =>{
  fetch('http://localhost:4000/orders'  )
  .then(response=>response.json())
  .then(response=>this.setState({data:response.data}))
  .catch(err=>console.error(err))
}
  render(){
  const {state, setState} = this.state;

  return (
    <MaterialTable
    icons={tableIcons}
      title="My Orders"
      columns={this.state.columns}
      data={this.state.data}
      actions={[
        {
          icon: Save,
          tooltip: 'Save User',
          onClick: (event, rowData) => alert("You saved " + rowData.name)
     }
    , {
      icon: Done,
      tooltip: 'Done',
      onClick: (event, rowData) => alert("You saved " + rowData.name)
 }
    ]}
      editable={{
        onRowAdd: newData =>
          new Promise(resolve => {
            setTimeout(() => {
              resolve();
              setState(prevState => {
                const data = [...prevState.data];
                data.push(newData);
                return { ...prevState, data };
              });
            }, 600);
          }),
        onRowUpdate: (newData, oldData) =>
          new Promise(resolve => {
            setTimeout(() => {
              resolve();
              if (oldData) {
                setState(prevState => {
                  const data = [...prevState.data];
                  data[data.indexOf(oldData)] = newData;
                  return { ...prevState, data };
                });
              }
            }, 600);
          }),
        onRowDelete: oldData =>
          new Promise(resolve => {
            setTimeout(() => {
              resolve();
              setState(prevState => {
                const data = [...prevState.data];
                data.splice(data.indexOf(oldData), 1);
                return { ...prevState, data };
              });
            }, 600);
          }),
          
          
        }}
         
      options={{
        exportButton: true,
        filtering: true,
        grouping: true,
        sorting: true,
        rowStyle:{
          backgroundColor: '#fafafa'
        }
      }}
      
    />
  );
}
 }
 export default MyOrders;
