import React from 'react';
import clsx from 'clsx';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/styles';
import { Card, CardContent, Grid, Typography, Avatar } from '@material-ui/core';
import {LinearProgress} from '@material-ui/core'
import PaymentIcon from '@material-ui/icons/Payment';
import axios from 'axios';
import * as OrderAPI from 'views/Orders/OrderManagement/api/OrderAPI';

const useStyles = makeStyles(theme => ({
  root: {
    height: '100%'
  },
  progress: {
    marginTop: theme.spacing(3)
  },
  content: {
    alignItems: 'center',
    display: 'flex'
  },
  title: {
    fontWeight: 700
  },
  avatar: {
    backgroundColor: theme.palette.error.main,
    height: 56,
    width: 56
  },
  icon: {
    height: 32,
    width: 32
  },
  difference: {
    marginTop: theme.spacing(2),
    display: 'flex',
    alignItems: 'center'
  },
  differenceIcon: {
    color: theme.palette.error.dark
  },
  differenceValue: {
    color: theme.palette.error.dark,
    marginRight: theme.spacing(1)
  }
}));
const getCountNborders = () => {


  axios.get('http://localhost:4000/orders/countall')
.then(function (response) {

 console.log(response.data.count)
 return response.data.count;


})
.catch(function (error) {

  console.log(error);
})

}
const TotalOrders = props => {
  const { className, ...rest } = props;

  const classes = useStyles();
const nb=OrderAPI.getCountNborders();
  return (
    <Card
      {...rest}
      className={clsx(classes.root, className)}
    >
      <CardContent>
        <Grid
          container
          justify="space-between"
        >
          <Grid item>
            <Typography
              className={classes.title}
              color="textSecondary"
              gutterBottom
              variant="body2"
            >
             Total Orders
            </Typography>
<getCountNborders></getCountNborders>
          </Grid>
          <Grid item>
            <Avatar className={classes.avatar}>
              <PaymentIcon className={classes.icon} />
            </Avatar>
          </Grid>
        </Grid>

        <LinearProgress
          className={classes.progress}
          value={5}
          variant="determinate"
        ></LinearProgress>

      </CardContent>
    </Card>
  );
};

TotalOrders.propTypes = {
  className: PropTypes.string
};

export default TotalOrders;
