import axios from 'axios';

function sendDelete(userId){
    
    const baseUrl = `http://localhost:4000/orders/delete/`+userId   
     
    axios.get(baseUrl)
    .then(response =>{
      if (response.data.success) {
        alert(response.data.message)
      }
    })
    .catch ( error => {
      alert("Error on delete item")
    })
  }
  function sendCreate(){

   
    // url de backend
    const baseUrl = "http://localhost:4000/orders/create"
    // parameter data post
    const datapost = {
      Orderdate:this.state.Orderdate,
      Category:this.state.Category,
      Products:this.state.Products,
      Status:this.state.Status,
      Comment:this.state.Comment,
      qte:this.state.qte,
      pss:this.state.pss,
       
    }
  
    axios.post(baseUrl,datapost)
    .then(response => {
      if (response.data.success) {
       alert(response.data.message)
      }
      else {
        alert("Error when creating a new order ")
       
      }
    })
    .catch ( error => {
      alert(error)
      console.log(error)
    })
  
  }
  export default {sendDelete, sendCreate}
