import { Component } from "react";
import AddressForm from "views/Orders/components/AddressForm";
import React from 'react';
import { Paper } from "@material-ui/core";
import CssBaseline from '@material-ui/core/CssBaseline';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';

import Container from '@material-ui/core/Container';
class AddOrder extends Component{
    state={
        data:[]

    }
    componentDidMount(){this.getclientsDetails();
        
        
        }
    getclientsDetails= _ =>{
        fetch('http://localhost:4000/clients/list'  )
        .then(response=>response.json())
        .then(response=>this.setState({data:response.data}))
        .catch(err=>console.error(err))
      }
     
render(){

    return ( 
    
    
    <React.Fragment>
         <CssBaseline />
         {console.log(this.state.data)}
         <Container maxWidth="md" style={{padding:50}}>
        <Paper style={{padding:90}}>
        <Typography variant="h6" gutterBottom>
       General Information
      </Typography>
      <Grid container spacing={2}>
        <Grid item xs={12} sm={6}>
        <label>Name</label>
        {this.state.data.map(function (client) {
            return (
                <TextField
                required
                id="firstName"
                name="firstName"
                label={client.clientFirstName}
                fullWidth
               
                autoComplete="fname"
                disabled ={client.clientFirstName!=""?"disabled":""} 
              ></TextField>
            );
        })}
          
        </Grid>
        <Grid item xs={12} sm={6}>
            <label>Last Name</label>
            {this.state.data.map(function (client) {
            return (
          <TextField
            required
            id="lastName"
            name={client.clientName}
            label=""
            fullWidth
            autoComplete="lname"
            disabled ={client.clientName!=""?"disabled":""}  
            
          /> );
        })}
        </Grid>
        <Grid item xs={12}>
        {this.state.data.map(function (client) {
            return (
          <TextField
            required
            id="address1"
            name="address1"
            label="Address line 1"
            fullWidth
            autoComplete="billing address-line1"
            error
          ></TextField> 
          );
        })}
        </Grid>
        <Grid item xs={12}>
          <TextField
            id="address2"
            name="address2"
            label="Address line 2"
            fullWidth
            autoComplete="billing address-line2"
            
          />
         
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            required
            id="city"
            name="city"
            label="City"
            fullWidth
            autoComplete="billing address-level2"
            error
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField id="state" name="state" label="State/Province/Region" fullWidth />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            required
            id="zip"
            name="zip"
            label="Zip / Postal code"
            fullWidth
            autoComplete="billing postal-code"
            error
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            required
            id="country"
            name="country"
            label="Country"
            fullWidth
            autoComplete="billing country"
            error
            
          />
          
        </Grid>
        <Grid item xs={12}>
           
        </Grid>
      </Grid></Paper>
        
        </Container>
        </React.Fragment>)

}


}
export default AddOrder;
