import React, { useState } from 'react';
import clsx from 'clsx';
import { Component } from "react";
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/styles';
import {
  Card,
  CardHeader,
  CardContent,
  CardActions,
  Divider,
  Grid,
  Button,
  TextField
} from '@material-ui/core';
class AccountDetails extends Component{

  state={
    data:[],

  }

  componentDidMount(){
    this.getUserDetails();

      }
   getUserDetails= _ =>{
    const userNamesession = JSON.parse(localStorage.getItem('user'));
    console.log(userNamesession)
        fetch('http://localhost:4000/api/user/listByUsername/'+userNamesession.username )
        .then(response=>response.json())
        .then(response=>this.setState({data:response.data}))
        .catch(err=>console.error(err))
      }




      render(){


        return ( 

    <Card>

      <form
        autoComplete="off"
        noValidate
      >
        <CardHeader
          subheader="The information can be edited"
          title="Profile"
        />
        <Divider />
        <CardContent>
          <Grid
            container
            spacing={3}
          >

            <Grid
              item
              md={6}
              xs={12}
            >
               {this.state.data.map(function (user) {
            return (
            <TextField
              fullWidth
              helperText="Please specify the first name"
              label="First name"
              margin="dense"
              name="firstName"

              required
              value={user.FirstName}
              variant="outlined"
            />)})}

            </Grid>
            <Grid
              item
              md={6}
              xs={12}
            >
                {this.state.data.map(function (user) {
            return (
              <TextField
                fullWidth
                label="Last name"
                margin="dense"
                name="lastName"

                required
                value={user.LastName}
                variant="outlined"
                />)})}
            </Grid>
            <Grid
              item
              md={6}
              xs={12}
            >
              <TextField
                fullWidth
                label="Email Address"
                margin="dense"
                name="email"

                required
                value={""}
                variant="outlined"
              />
            </Grid>
            <Grid
              item
              md={6}
              xs={12}
            >
              <TextField
                fullWidth
                label="Phone Number"
                margin="dense"
                name="phone"

                type="number"
                value={""}
                variant="outlined"
              />
            </Grid>
            <Grid
              item
              md={6}
              xs={12}
            >
              <TextField
                fullWidth
                label="Select State"
                margin="dense"
                name="state"

                required

                // eslint-disable-next-line react/jsx-sort-props

                value={""}
                variant="outlined"
              >

              </TextField>
            </Grid>
            <Grid
              item
              md={6}
              xs={12}
            >
              <TextField
                fullWidth
                label="Country"
                margin="dense"
                name="country"

                required
                value={""}
                variant="outlined"
              />
            </Grid>
          </Grid>
        </CardContent>
        <Divider />
        <CardActions>
          <Button
            color="primary"
            variant="contained"
          >
            Save details
          </Button>
        </CardActions>
      </form>
    </Card>



    )

  }


  }

export default AccountDetails;
