import React, { useState } from "react";
import TextareaAutosize from '@material-ui/core/TextareaAutosize';
import "./TableOrder.css";
import DateFnsUtils from '@date-io/date-fns';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import TextField from '@material-ui/core/TextField';

import {
  KeyboardDatePicker,
  MuiPickersUtilsProvider
} from '@material-ui/pickers';
const _defaultCosts = [
  
  {
    name: "lentille",
    price: 40
  }
];

const CostTable = () => {
  const [costs, setCosts] = useState(_defaultCosts);

  const handleCostsChange = event => {
    const _tempCosts = [...costs];
    _tempCosts[event.target.dataset.id][event.target.name] = event.target.value;

    setCosts(_tempCosts);
    
  };

  const addNewCost = () => {
    setCosts(prevCosts => [...prevCosts, { name: "", price: 0 }]);
  };

  const getTotalCosts = () => {
    console.log(costs);
    return costs.reduce((total, item) => {
      return total + Number(item.price);
   
    }, 0);
  };
  const [selectedDate, setSelectedDate] = React.useState(new Date());

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };
  const [category, setCategory] = React.useState('');

  const handleChange = (event) => {
    setCategory(event.target.value);
  
  };
  const [products, setProducts] = React.useState('');
  const handleChangeProducts = (event) => {
    setProducts(event.target.value);
  
  };
  return (
    <div className="table">
      <div className="table-title">Create new order</div>
      <div className="table-content">
        <div className="table-header">
          <div className="table-row">
            <div className="table-data">
              <div>Category</div>
              
            </div>
            <div className="table-data">
              <div style={{flexDirection: 'row-reverse'}}>Products</div>
              
            </div>
            <div className="table-data">
              <div>Order date</div>
            </div>
            <div className="table-data">
              <div>Quantity</div>
            </div>
            <div className="table-data">
              <div>sph </div>
            </div>
            <div className="table-data">
              <div>Comment</div>
            </div>
          </div>
        </div>
        <div className="table-body">
          {costs.map((item, index) => (
            <div className="table-row" key={index}>
              <div className="table-data">
                
                
              <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={category}
                onChange={handleChange}
        >
                <MenuItem value={'produit'}>Produit</MenuItem>
                <MenuItem value={'Lentille'}>Lentille</MenuItem>
          
        </Select>
              </div>
              <div className="table-data">
                
              <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={products}
                onChange={handleChangeProducts}
        >
                <MenuItem value={'Releasy'}>Releasy</MenuItem>
                <MenuItem value={'Skysoft'}>Skysoft</MenuItem>
          
        </Select>
              
              </div>
              <div className="table-data">
              <MuiPickersUtilsProvider utils={DateFnsUtils}>
                      <KeyboardDatePicker
                  
                  id="date-picker-dialog"
                  format="MM/dd/yyyy"
                  value={selectedDate}
                  onChange={handleDateChange}
                  KeyboardButtonProps={{
                    'aria-label': 'change date',
                  }}
                /></MuiPickersUtilsProvider>
              </div>
              <div className="table-data">
              <TextField
          id="outlined-number"
          name="name"
          type="number"
          
          variant="outlined"
          //onChange={handleCostsChange}
        />

            
              </div>
              <div className="table-data">
              <select
                 name="sph"
                 data-id={index}
                 type="text"
                 value={item.sph}
                 onChange={handleCostsChange}
                
                >
              <option value="-1,00">-1,00</option>
              <option value="-1,25">-1,25</option>
              <option value="-1,25">-1,25</option>

              <option value="-1,5">-1,5</option>
              <option value="-1,75">-1,75</option>
              <option value="-2,00">-2,00</option>

              <option value="-2,25">-2,25</option>

              <option value="-2,50">-2,50</option>

              <option value="-2,75">-2,75</option>

              <option value="-3,00">-3,00</option>

              <option value="-3,25">-3,25</option>

              <option value="--3,50">-3,50</option>

              <option value="-3,75">-3,75</option>

              <option value="-4,00">-4,00</option>

              <option value="-4,25">-4,25</option>

              <option value="-4,50">-4,50</option>

              <option value="-4,75">-4,75</option>

              <option value="-5,00">-5,00</option>

              <option value="-5,25">-5,25</option>
              <option value="-5,50">-5,50</option>
              <option value="-5,75">-5,75</option>

              <option value="-6,00">-6,00</option>


             
              </select>
              </div>
              <div className="table-data">
              <TextareaAutosize
                  rowsMax={4}
                  name="comment"
                  data-id={index}
                  type="text"
                 
                  onChange={handleCostsChange}
                />
              </div>
            </div>
          ))}
          <div className="table-row">
            <div className="table-data">
              <button style={{backgroundColor: '#04E605', position: 'absolute', color: 'white' ,fontSize:'30 px'}} onClick={addNewCost}>  + Add   </button>
              
            </div>
          </div>
          
        </div>
        <div className="table-footer">
          <div className="table-row">
            <div className="table-data">
              <div>Total</div>
            </div>
            <div className="table-data">
              <div>{getTotalCosts()}</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CostTable;
