import { Component } from "react";
import AddressForm from "views/Orders/components/AddressForm";
import React from 'react';
import { Paper, Button } from "@material-ui/core";
import CssBaseline from '@material-ui/core/CssBaseline';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import Switch from '@material-ui/core/Switch';
import AddBox from '@material-ui/icons/AddBox';
import Container from '@material-ui/core/Container';
import UsersToolbar from "views/UserList/components/UsersToolbar"
class AddOrder extends Component{
    state={
        data:[],
        checked:false,
        setChecked:"",
        data2:[],
    }
    
    componentDidMount(){this.getclientsDetails();
        
        }
        appendChild = () => {
            let { data2 } = this.state;
            data2.push(data2.length); 
            this.setState({data2});
          };
    getclientsDetails= _ =>{
        fetch('http://localhost:4000/clients/list'  )
        .then(response=>response.json())
        .then(response=>this.setState({data:response.data}))
        .catch(err=>console.error(err))
      }
       
render(){
     
  
    
    return ( 
    
    
    <React.Fragment>
         <CssBaseline />
        
       
         <Container maxWidth="md" style={{padding:50}}>
       
        <Paper style={{padding:60}}>
        <Typography variant="h6" gutterBottom>
       General Information
      </Typography>
      <Grid container spacing={2}>
        <Grid item xs={12} sm={6}>
        
        {this.state.data.map(function (client) {
            return (
                <TextField
                required
                id="firstName"
                name="firstName"
                label=" First Name"
                fullWidth
                defaultValue={client.clientFirstName}
                autoComplete="fname"
                
              ></TextField>
            );
        })}
          
        </Grid>
        <Grid item xs={12} sm={6}>
            
            {this.state.data.map(function (client) {
            return (
          <TextField
            required
            id="lastName"
            name="lastName"
            label="Last Name"
            defaultValue={client.clientName}
            fullWidth
            autoComplete="lname"
             
            
          /> );
        })}
        </Grid>
        <Grid item xs={12}>
        {this.state.data.map(function (client) {
            return (
          <TextField
            required
            id="address1"
            name="address1"
            defaultValue={client.clientAdresse}
            label="Address Line 1"
            fullWidth
            autoComplete="billing address-line1"
            
            error
          ></TextField> 
          );
        })}
        </Grid>
        <Grid item xs={12}>
          <TextField
            id="address2"
            name="address2"
            label="Address line 2"
            fullWidth
            autoComplete="billing address-line2"
            
          />
         
        </Grid>
        <Grid item xs={12} sm={6}>
        {this.state.data.map(function (client) {
            return (
          <TextField
            required
            id="city"
            name="city"
            label="City"
            defaultValue={client.city}
            fullWidth
            autoComplete="billing address-level2"
            error
            
          /> );
        })}
        </Grid>
        <Grid item xs={12} sm={6}>
        {this.state.data.map(function (client) {
            return (
          <TextField required id="phoneNumber" name="phoneNumber" label="Phone Number" fullWidth    defaultValue={client.clientPhoneNumber} />);
        })}
        </Grid>
        <Grid item xs={12} sm={6}>
             {this.state.data.map(function (client) {
            return (
          <TextField
            required
            id="zip"
            name="zip"
            label="Postal code"
            fullWidth
            defaultValue={client.postalCode}
            autoComplete="billing postal-code"
            error
          /> );
        })}
        </Grid>
        <Grid item xs={12} sm={6}>
        {this.state.data.map(function (client) {
            return (
          <TextField
            required
            id="country"
            name="country"
            label="Country"
            fullWidth
            autoComplete="billing country"
            error
            
          />
           );
        })}
      
        </Grid>
        
      </Grid></Paper>
        
        </Container>
        <Container maxWidth="lg" style={{paddingLeft:30,paddingRight:30}}>
       
       <Paper style={{paddingTop:30,paddingBottom:30}}>
       <Container maxWidth="lg" >
       <div style={{ height: '42px',
    display: 'flex',
    alignItems: 'center',
    marginTop: 1}}>
       
       <span style={{flexGrow: 1}} />
    
    <Button
        variant="contained"
        
        size="large"
        style={{ borderColor: '#0063cc',font: '#0069d9',
        borderColor: '#0062cc',
        boxShadow: 'none',
        
    }}
        startIcon={<AddBox />}
       
            onClick={()=>{}}
            >ADD</Button> 
            </div>
            <table>
        <thead>
          <th>Type</th>
          <th>Position</th>
        </thead>
        <tbody>
          {this.state.data.map(id => (
            <Row id = {id} />
          ))}
        </tbody>
      </table>
       </Container>
           </Paper></Container>
        </React.Fragment>)

}


}

const Row = ({ id }) => (
    <tr>
      <td>
        <input type="text" id={`select-type-${id}`} />
      </td>
      <td>
        <input type="text" id={`select-position-${id}`} />
      </td>
    </tr>
  );
export default AddOrder;
