import { Component } from "react";
import AddressForm from "views/Orders/components/AddressForm";
import React from 'react';
import { Paper, Button } from "@material-ui/core";
import CssBaseline from '@material-ui/core/CssBaseline';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import Switch from '@material-ui/core/Switch';
import AddBox from '@material-ui/icons/AddBox';
import Container from '@material-ui/core/Container';
import UsersToolbar from "views/UserList/components/UsersToolbar"
import Zoom from '@material-ui/core/Zoom';
import MyOrders from './LentilleOrders'
import SaveIcon from '@material-ui/icons/Save';
import CancelIcon from '@material-ui/icons/Cancel';
import DoneAllIcon from '@material-ui/icons/DoneAll';
import CostTable from '../TableOrder/TableOrder'
import { Link as RouterLink, withRouter } from 'react-router-dom';
class AddOrder extends Component{
    state={
        data:[],
        checked:false,
        setChecked:"",
        like:0,
        checked:false,
    }
     handleChange = () => {
      this.state==false?this.setState({checked:false}):this.setState({checked:true})
    };
    
    
    componentDidMount(){this.getclientsDetails();
        
        
        }
    getclientsDetails= _ =>{
        fetch('http://localhost:4000/api/user/list'  )
        .then(response=>response.json())
        .then(response=>this.setState({data:response.data}))
        .catch(err=>console.error(err))
      }
       
render(){
     
  
    
    return ( 
    
    
    <React.Fragment>
         <CssBaseline />
        
       
         <Container maxWidth="md" style={{padding:50}}>
       
        <Paper style={{padding:60}}>
        <Typography variant="h6" gutterBottom>
       General Information
      </Typography>
      <Grid container spacing={2}>
        <Grid item xs={12} sm={6}>
        
        {this.state.data.map(function (client) {
            return (
                <TextField
                required
                id="firstName"
                name="firstName"
                label=" First Name"
                fullWidth
                defaultValue={client.FirstName}
                autoComplete="fname"
                
              ></TextField>
            );
        })}
          
        </Grid>
        <Grid item xs={12} sm={6}>
            
            {this.state.data.map(function (client) {
            return (
          <TextField
            required
            id="lastName"
            name="lastName"
            label="Last Name"
            defaultValue={client.Name}
            fullWidth
            autoComplete="lname"
             
            
          /> );
        })}
        </Grid>
        <Grid item xs={12}>
        {this.state.data.map(function (client) {
            return (
          <TextField
            required
            id="address1"
            name="address1"
            defaultValue={client.Adresse}
            label="Address Line 1"
            fullWidth
            autoComplete="billing address-line1"
            
            
          ></TextField> 
          );
        })}
        </Grid>
        <Grid item xs={12}>
          <TextField
            id="address2"
            name="address2"
            label="Address line 2"
            fullWidth
            autoComplete="billing address-line2"
            
          />
         
        </Grid>
        <Grid item xs={12} sm={6}>
        {this.state.data.map(function (client) {
            return (
          <TextField
            required
            id="city"
            name="city"
            label="City"
            defaultValue={client.city}
            fullWidth
            autoComplete="billing address-level2"
            
            
          /> );
        })}
        </Grid>
        <Grid item xs={12} sm={6}>
        {this.state.data.map(function (client) {
            return (
          <TextField required id="phoneNumber" name="phoneNumber" label="Phone Number" fullWidth    defaultValue={client.PhoneNumber} />);
        })}
        </Grid>
        <Grid item xs={12} sm={6}>
             {this.state.data.map(function (client) {
            return (
          <TextField
            required
            id="zip"
            name="zip"
            label="Postal code"
            fullWidth
            defaultValue={client.PostalCode}
            autoComplete="billing postal-code"
            
          /> );
        })}
        </Grid>
        <Grid item xs={12} sm={6}>
        {this.state.data.map(function (client) {
            return (
          <TextField
            required
            id="country"
            name="country"
            label="Country"
            fullWidth
            defaultValue={client.country}
            autoComplete="billing country"
          
            
          />
           );
        })}
      
        </Grid>
        
      </Grid></Paper>
        
        </Container>
        <Container maxWidth="lg" style={{paddingLeft:30,paddingRight:30}}>
       
       <Paper style={{paddingTop:30,paddingBottom:30}}>
       <Container maxWidth="lg" >
       <div style={{ height: '42px',
    display: 'flex',
    alignItems: 'center',
    marginTop: 1}}>
       
       <span style={{flexGrow: 1}} />
    
    <Button
        variant="contained"
        
        size="large"
        style={{ borderColor: '#0063cc',font: '#0069d9',
        borderColor: '#0062cc',
        boxShadow: 'none',
        
    }}
        startIcon={<AddBox />}
       
            onClick={()=>this.handleChange()}
            >ADD</Button> 
            </div>
       </Container>
           </Paper>
           
           
           <Zoom in={this.state.checked}>
          <div elevation={20} >
         <CostTable></CostTable>
         <br></br>

         <Container maxWidth="xs" >
           
         <Paper style={{paddingTop:10,paddingBottom:10}}>
         
          

<Button
 size="medium"  style={{font: '#0069d9',
 borderColor: 'red',
 boxShadow: 'none',

 
}}
                    component={RouterLink}
                    to="/GeneralInformation"
                    variant="contained"
                    
                    startIcon={<CancelIcon />}
                     
                  >
                   Cancel
                  </Button>
<Button  variant="contained"
        
        size="medium"  style={{font: '#0069d9',
        boxShadow: 'none',
        
    }} color="primary" startIcon={<SaveIcon />} 
    >Save </Button> 
<Button  variant="contained"
      
        size="medium"  style={{font: '#0069d9',
        boxShadow: 'none',
        
    }} color="secondary" startIcon={<DoneAllIcon />} 
    >Submit </Button> 
    </Paper>
          </Container>
          </div>
          
        </Zoom>
          
           </Container>


        </React.Fragment>
        
        
        
        
        )

}


}
export default AddOrder;
