import React, { Component } from 'react';
import { Button, Table, Select } from "@material-ui/core";
import "./TableOrder.css";
import TextField from '@material-ui/core/TextField';
import { InputGroup, InputGroupAddon, InputGroupText, Input } from 'reactstrap'
import MenuItem from '@material-ui/core/MenuItem';
import {
    KeyboardDatePicker,
    MuiPickersUtilsProvider
  } from '@material-ui/pickers';
import DateFnsUtils from '@date-io/date-fns';
class Test extends Component {
    constructor(props) {
        super(props);
        this.state = {
            rowData: [],
            Produits: [],
            Quantite: "",
            Prix: "",
            id: 0,
            orderDate:""
        };

        this.handleRowChange = this.handleRowChange.bind(this);
        this.handleRowDelete = this.handleRowDelete.bind(this);
        this.handleRowAdd = this.handleRowAdd.bind(this);
        this.getTotal = this.getTotal.bind(this);
        this.pushToCaller = this.pushToCaller.bind(this);
    }
    handleQuantiteChange = (e) => {
        this.setState({
            Quantite: e.target.value
        }, this.pushToCaller);
    }
    handleselectprdtChange = (e) => {
        this.setState({
            selectprdt: e.target.value
        }, this.pushToCaller);
        console.log(e.target.value)
    }
    handleselectDateChange = (e) => {
        this.setState({
            orderDate: e.target
        }, this.pushToCaller);
        console.log(e)
    }
    handlePrixChange = (e) => {
        this.setState({
            Prix: e.target.value
        }, this.pushToCaller);
    }

    pushToCaller = () => {
        this.handleRowChange(this.state.id, {
            Quantite: parseInt(this.state.Quantite, 10),
            selectprdt: this.state.selectprdt,
            Prix: parseFloat(this.state.Prix),
        });
    }


    render() {

        return (<div className="animated fadeIn">



            <div className="table">
                <div className="table-title">Create new Lenses Orders</div>
                <div className="table-content">
                    <div className="table-header">
                        <div className="table-row">
                            <div className="table-data">
                                <div>Category</div>

                            </div>
                            <div className="table-data">
                                <div style={{ display: 'flex' }}>Products</div>

                            </div>
                            <div className="table-data">
                                <div>Order date</div>
                            </div>
                            <div className="table-data">
                                <div>Quantity</div>
                            </div>
                            <div className="table-data">
                                <div>sph </div>
                            </div>
                            <div className="table-data">
                                <div>Comment</div>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="table-body">
                    {this.state.rowData.map((index) =>

                        <div className="table-row" key={index} id={index}   >



                            <div className="table-data">
                                <Select name="category" id="category"

                                    onChange={this.handleselectprdtChange} >

                                    <MenuItem value={'Lenses'}>Lenses</MenuItem>

                                </Select>
                            </div>
                            <div className="table-data">

                                <Select
                                    labelId="product"
                                    id="product"

                                    onChange={this.handleselectprdtChange}
                                >
                                    <MenuItem value={'Skysoft'}>Skysoft</MenuItem>

                                </Select>

                            </div>
                            <div className="table-data">
                                <MuiPickersUtilsProvider utils={DateFnsUtils}>
                                    <KeyboardDatePicker

                                        id="orderDate"
                                        format="MM/dd/yyyy"
                                        value={"04/12/2019"}
                                        onChange={this.handleselectDateChange} 
                                        KeyboardButtonProps={{
                                            'aria-label': 'change date',
                                        }}
                                    /></MuiPickersUtilsProvider>
                            </div>
                            <div className="table-data"><TextField type="number"
                                placeholder="0" value={this.state.rowData.Quantite} onChange={this.handleQuantiteChange} /></div>
                            <div className="table-data">
                                <InputGroup ><TextField type="text"
                                    value={this.state.rowData.Prix} onChange={this.handlePrixChange} />
                                    <InputGroupAddon addonType="prepend">
                                        <InputGroupText><i ></i></InputGroupText>
                                    </InputGroupAddon>
                                </InputGroup >
                            </div>

                            <div className="table-data">
                                <p >{this.state.Quantite * this.state.Prix}  </p>

                            </div>
                            <div className="table-data">
                                <Button onClick={this.handleRowDelete} active  >Effacer</Button>
                            </div> </div>)}

                    <div className="table-row" >


                        <div className="table-data"><Button onClick={this.handleRowAdd} >Ajouter une ligne</Button></div>
                    </div>
                </div>

                <tfoot>
                    <tr>

                        <th></th>
                        <th >Grand total :</th>
                        <th>{this.getTotal()} </th>
                        <th></th>
                    </tr>
                </tfoot>

            </div>


        </div>);
    }
    getTotal() {
        let grandTotal = 0;
        const rowTotals = this.state.rowData.map(row => this.state.Quantite * this.state.Prix);
        if (rowTotals.length > 0) {
            grandTotal = rowTotals.reduce((acc, val) => acc + val);
        }
        return grandTotal;
    }
    handleRowChange(row, data) {
        const rowDataCopy = this.state.rowData.slice(0);
        rowDataCopy[row] = data;
        this.setState({
            rowData: rowDataCopy

        });
    }
    handleRowDelete(row) {
        const rowDataCopy = this.state.rowData.slice(0);
        rowDataCopy.splice(row, 1);
        this.setState({
            rowData: rowDataCopy
        });
    }
    handleRowAdd() {
        let id = this.state.id;
        id = id++;
        const rowDataCopy = this.state.rowData.slice(0);
        rowDataCopy.push({ selectprdt: '', Quantite: "", Prix: "" });
        this.setState({
            rowData: rowDataCopy,
            id: id
        });
    }

}
export default Test;
