import React, { Component } from 'react';
import { Button, Table, Select, Paper } from "@material-ui/core";
import "./TableOrder.css";
import TextField from '@material-ui/core/TextField';
import Container from '@material-ui/core/Container';
import SaveIcon from '@material-ui/icons/Save';
import CancelIcon from '@material-ui/icons/Cancel';
import DoneAllIcon from '@material-ui/icons/DoneAll';
import { Link as RouterLink } from 'react-router-dom';
import MenuItem from '@material-ui/core/MenuItem';
import * as OrderAPI from "../api/OrderAPI"
import AddShoppingCart from '@material-ui/icons/AddShoppingCart';
import DeleteSweep from '@material-ui/icons/DeleteSweep'
import {
  KeyboardDatePicker,
  MuiPickersUtilsProvider
} from '@material-ui/pickers';
import DateFnsUtils from '@date-io/date-fns';
class OrderProduct extends Component {
  constructor(props) {
    super(props);
    this.state = {
      rowData: [],
      Produits: [],
      Quantite: 0,
      id: 0,
      orderDate: new Date(),
      volume: "",
      comment: "",
      product: "",
      categorie: ""
    };

    this.handleRowChange = this.handleRowChange.bind(this);
    this.handleRowDelete = this.handleRowDelete.bind(this);
    this.handleRowAdd = this.handleRowAdd.bind(this);
    this.pushToCaller = this.pushToCaller.bind(this);
  }
  handleQuantiteChange = (e) => {
    this.setState({
      Quantite: e.target.value
    }, this.pushToCaller);
  }
  handleCategorieChange = (e) => {
    this.setState({
      categorie: e.target.value
    }, this.pushToCaller);
    console.log(e.target.value)
  }
  handleProductChange = (e) => {
    this.setState({
      product: e.target.value
    }, this.pushToCaller);
    console.log(e.target.value)
  }
  handleSphChange = (e) => {
    this.setState({
      sph: e.target.value
    }, this.pushToCaller);
    console.log(e.target.value)
  }
  handleCommentChange = (e) => {
    this.setState({
      comment: e.target.value
    }, this.pushToCaller);

  }
  handleselectDateChange = (e) => {
    // let date= parseInt(e.getFullYear() + "-"+ e.getMonth()+1) +"-"+ e.getDate();
    let date = new Date(e.getFullYear(), e.getMonth() + 1, e.getDate())
    this.setState({
      orderDate: date
    }, this.pushToCaller);

    console.log(date)
  }
  handlePrixChange = (e) => {
    this.setState({
      Prix: e.target.value
    }, this.pushToCaller);
  }

  pushToCaller = () => {
    this.handleRowChange(this.state.id, {
      Quantite: parseInt(this.state.Quantite, 10),
      categorie: this.state.categorie,
      product: this.state.product,
      orderDate: this.state.orderDate,
      sph: this.state.sph,
      comment: this.state.comment,

    });
  }
  saveData = () => {
    const userId = JSON.parse(localStorage.getItem('user'));

    this.state.rowData.forEach(order => {
      OrderAPI.sendCreate(order.orderDate, order.categorie, order.product, "Draft", order.comment, order.Quantite, order.sph, "Produit", userId.id)

    });

  }
  submitData = () => {
    const userId = JSON.parse(localStorage.getItem('user'));

    this.state.rowData.forEach(order => {
      OrderAPI.sendCreate(order.orderDate, order.categorie, order.product, "WaitingValidation", order.comment, order.Quantite, order.sph, "Produit", userId.id)

    });
  }

  render() {

    return (<div className="animated fadeIn">



      <div className="table">
        <div className="table-title">Create new Product Orders</div>
        <div className="table-content">
          <div className="table-header">
            <div className="table-row">
              <div className="table-data">
                <div>Category</div>

              </div>
              <div className="table-data">
                <div style={{ display: 'flex' }}>Products</div>

              </div>
              <div className="table-data">
                <div>Order date</div>
              </div>
              <div className="table-data">
                <div>Quantity</div>
              </div>
              <div className="table-data">
                <div>Volume </div>
              </div>
              <div className="table-data">
                <div>Comment</div>
              </div>
            </div>
          </div>
        </div>
        <div className="table-body">
          {this.state.rowData.map((index) =>

            <div className="table-row" key={index} id={index}   >



              <div className="table-data">
                <Select name="category" id="category"
                defaultValue={'Product'}  
                  onChange={this.handleCategorieChange} >

                  <MenuItem value={'Product'} displayEmpty>Product</MenuItem>

                </Select>
              </div>
              <div className="table-data">

                <Select
                  labelId="product"
                  id="product"

                  onChange={this.handleProductChange}
                >
                  <MenuItem value={'Releasy'}>Releasy</MenuItem>

                </Select>

              </div>
              <div className="table-data">
                <MuiPickersUtilsProvider utils={DateFnsUtils}>
                  <KeyboardDatePicker

                    id="orderDate"
                    format="yyyy-MM-dd"
                    value={this.state.orderDate != "" ? this.state.orderDate : new Date()}
                    onChange={this.handleselectDateChange}
                    KeyboardButtonProps={{
                      'aria-label': 'change date',
                    }}
                  /></MuiPickersUtilsProvider>
              </div>
              <div className="table-data">
                <TextField
                  id="quantity"
                  name="quantity"
                  type="number"
                  variant="outlined"
                  value={this.state.rowData.Quantite}
                  onChange={this.handleQuantiteChange}
                />
              </div>
              <div className="table-data">
                <Select
                  name="volume"
                  data-id={index}
                  value={this.state.rowData.volume}
                  onChange={this.handleSphChange}
                  disabled=  {this.state.Quantite>0?false:true}
                >
                  <MenuItem value="25 ML">25 ML</MenuItem>
                  <MenuItem value="50 ML">50 ML</MenuItem>
                  <MenuItem value="75 ML">75 ML</MenuItem>
                  <MenuItem value="100 ML">100 ML</MenuItem>

                </Select>
              </div>
              <div className="table-data">
                <TextField
                  multiline
                  rows={2}
                  rowsMax={4}
                  name="comment"
                  data-id={index}
                  type="text"
                  //value={this.state.rowData.comment}
                  onChange={this.handleCommentChange}
                // value={this.state.rowData.Prix} onChange={this.handlePrixChange} 
                // onChange={handleCostsChange}
                />
              </div>



            </div>)}

          <div className="table-row" >


            <div className="table-data"><Button onClick={this.handleRowAdd} startIcon={
              <AddShoppingCart />
            } >Add new Order Line</Button> <Button onClick={this.handleRowDelete} active startIcon={
              <DeleteSweep />} >Delete Order Line</Button></div>
          </div>
        </div>

        <tfoot>
          <tr>

          </tr>
        </tfoot>

      </div>

      <Container maxWidth="xs" >
        <center>
        <label style={{ color: 'red' }}> Please check if all values are correct</label><br></br>
                    <label style={{ color: 'red' }}>The comment length should be between 0 and 30 characters</label>
          <Paper style={{ paddingTop: 5, paddingBottom: 5 }}>
            <Button
              size="small" style={{
                font: '#0069d9',
                borderColor: 'red',
                boxShadow: 'none',
              }}
              component={RouterLink}
              to="/home"
              variant="contained"
              startIcon={
                <CancelIcon />
              }
            >
              Cancel
                  </Button>
            <Button variant="contained"
              size="small" style={{
                font: '#0069d9',
                boxShadow: 'none',
              }} color="primary" startIcon={
                <SaveIcon />
              }
              disabled={this.state.rowData.length>0?false:true}
              onClick={this.saveData}
            >Save </Button>
            <Button variant="contained"
              size="small" style={{
                font: '#0069d9',
                boxShadow: 'none',
              }} color="secondary" startIcon={
                <DoneAllIcon />
              }
              disabled={this.state.rowData.length>0?false:true}
              onClick={this.submitData}
            >Submit</Button>
          </Paper>
        </center>
      </Container>

    </div>);
  }



  handleRowChange(row, data) {
    const rowDataCopy = this.state.rowData.slice(0);
    rowDataCopy[row] = data;
    this.setState({
      rowData: rowDataCopy

    });
  }
  handleRowDelete(row) {
    const rowDataCopy = this.state.rowData.slice(0);
    rowDataCopy.splice(row, 1);
    this.setState({
      rowData: rowDataCopy
    });
  }
  handleRowAdd() {
    let id = this.state.id;
    id = id++;
    const rowDataCopy = this.state.rowData.slice(0);
    rowDataCopy.push({ categorie: this.state.categorie, Quantite: this.state.Quantite, product: this.state.product, orderDate: this.state.orderDate, sph: this.state.sph, comment: this.state.comment });
    this.setState({
      rowData: rowDataCopy,
      id: id
    });
    console.log(this.state.rowData)
  }

}
export default OrderProduct;
