import axios from 'axios';
import { Component } from 'react';
class OrderAPI extends Component {
  sendDelete(userId){
    
    const baseUrl = `http://localhost:4000/orders/delete/`+userId   
     
    axios.get(baseUrl)
    .then(response =>{
      if (response.data.success) {
        alert(response.data.message)
      }
    })
    .catch ( error => {
      alert("Error on delete item")
    })
  }
  sendCreate(order){

   
    // url de backend
    const baseUrl = "http://localhost:4000/orders/create"
    // parameter data post
    const datapost = {
      Orderdate:order.Orderdate,
      Category:order.Category,
      Products:order.Products,
      Status:order.Status,
      Comment:order.Comment,
      qte:order.qte,
      pss:order.pss,
       
    }
  
    axios.post(baseUrl,datapost)
    .then(response => {
      if (response.data.success) {
       alert(response.data.message)
      }
      else {
        alert("Error when creating a new order ")
       
      }
    })
    .catch ( error => {
      alert(error)
      console.log(error)
    })
  
  }}
  export default  OrderAPI
