import axios from 'axios';

 
 
export const  sendDelete=(userId,OrderType)=>{
    
    const baseUrl = `http://localhost:4000/orders`+OrderType+`/delete/`+userId   
     
    axios.get(baseUrl)
    .then(response =>{
      if (response.data.success) {
        alert(response.data.message)
      }
    })
    .catch ( error => {
      alert("Error on delete item")
    })
  }
  export const  sendCreate=(orderdate,Category,Products,Status,Comment,qte,pss,OrderType)=>{

   
    // url de backend
    const baseUrl = "http://localhost:4000/orders"+OrderType+"/create"
    // parameter data post
    if(OrderType=="Produit"){
    const datapost = {
      Orderdate:orderdate,
      Category:Category,
      Products:Products,
      Status:Status,
      Comment:Comment,
      qte:qte,
      volume:pss,
       
    }
    axios.post(baseUrl,datapost)
    .then(response => {
      if (response.data.success) {
       alert(response.data.message)
      }
      else {
        alert("Error when creating a new order ")
       
      }
    })
    .catch ( error => {
      alert(error)
      console.log(error)
    })
  }
  else {

    const datapost = {
      Orderdate:orderdate,
      Category:Category,
      Products:Products,
      Status:Status,
      Comment:Comment,
      qte:qte,
      pss:pss,
       
    }
    axios.post(baseUrl,datapost)
    .then(response => {
      if (response.data.success) {
       alert(response.data.message)
      }
      else {
        alert("Error when creating a new order ")
       
      }
    })
    .catch ( error => {
      alert(error)
      console.log(error)
    })

  }
  
   
  
  }
  export const  updateStatus=(orderid,OrderType)=>{


   
    // url de backend
    const baseUrl = "http://localhost:4000/orders"+OrderType+"/updateStatus/"+orderid
    // parameter data post
    const datapost = {
      Status:"WaitingValidation",
          
    }
  
    axios.post(baseUrl,datapost)
    .then(response => {
      if (response.data.success) {
       
       alert(response.data.message)
      }
      else {
        alert("Error when updating status ")
       
      }
    })
    .catch ( error => {
      alert(error)
      console.log(error)
    })
  
  }
  
export const  sendUpdate=(Orderdate,Category,Products,Status,Comment,qte,pss,orderid,OrderType)=>{

   
    // url de backend
    const baseUrl = "http://localhost:4000/orders"+OrderType+"/update/"+orderid
    // parameter data post
    if(OrderType=="Produit"){
    const datapost = {
      Orderdate:Orderdate,
      Category:Category,
      Products:Products,
      Status:Status,
      Comment:Comment,
      qte:qte,
      volume:pss,
       
    }
  
    axios.post(baseUrl,datapost)
    .then(response => {
      if (response.data.success) {
       alert(response.data.message)
      }
      else {
        alert("Error when updating data")
       
      }
    })
    .catch ( error => {
      alert(error)
      console.log(error)
    })}
    else {
      const datapost = {
        Orderdate:Orderdate,
        Category:Category,
        Products:Products,
        Status:Status,
        Comment:Comment,
        qte:qte,
        pss:pss,
         
      }
    
      axios.post(baseUrl,datapost)
      .then(response => {
        if (response.data.success) {
         alert(response.data.message)
        }
        else {
          alert("Error when updating data")
         
        }
      })
      .catch ( error => {
        alert(error)
        console.log(error)
      })


    }
  
  }
  
  export const getCountNborders=() =>{
  
   
    
    axios.get('http://localhost:4000/orders/countall')
  .then(function (response) {
    // handle success
   
   return response.data.count;
    
   
  })
  .catch(function (error) {
    
    console.log(error);
  })

 
  }
 

  export default  {sendDelete,sendCreate}
