import React, { Component } from 'react';
import { Button, Table, Select } from "@material-ui/core";
import "./TableOrder.css";
import TextField from '@material-ui/core/TextField';
import { InputGroup, InputGroupAddon, InputGroupText, Input } from 'reactstrap'
import MenuItem from '@material-ui/core/MenuItem';
import {
    KeyboardDatePicker,
    MuiPickersUtilsProvider
} from '@material-ui/pickers';
import DateFnsUtils from '@date-io/date-fns';
class Test extends Component {
    constructor(props) {
        super(props);
        this.state = {
            rowData: [],
            Produits: [],
            Quantite: "",
            id: 0,
            orderDate: new Date(),
            sph:"",
            comment:"",
            product:"",
            categorie:""
        };

        this.handleRowChange = this.handleRowChange.bind(this);
        this.handleRowDelete = this.handleRowDelete.bind(this);
        this.handleRowAdd = this.handleRowAdd.bind(this);
        this.getTotal = this.getTotal.bind(this);
        this.pushToCaller = this.pushToCaller.bind(this);
    }
    handleQuantiteChange = (e) => {
        this.setState({
            Quantite: e.target.value
        }, this.pushToCaller);
    }
    handleCategorieChange = (e) => {
        this.setState({
            categorie: e.target.value
        }, this.pushToCaller);
        console.log(e.target.value)
    }
    handleProductChange = (e) => {
        this.setState({
            product: e.target.value
        }, this.pushToCaller);
        console.log(e.target.value)
    }
    handleSphChange = (e) => {
        this.setState({
            sph: e.target.value
        }, this.pushToCaller);
        console.log(e.target.value)
    }
    handleCommentChange = (e) => {
        this.setState({
            comment: e.target.value
        }, this.pushToCaller);
        console.log(e.target.value)
    }
    handleselectDateChange = (e) => {
        this.setState({
            orderDate: document.getElementById('orderDate').value
        }, this.pushToCaller);
        console.log(e)
    }
    handlePrixChange = (e) => {
        this.setState({
            Prix: e.target.value
        }, this.pushToCaller);
    }

    pushToCaller = () => {
        this.handleRowChange(this.state.id, {
            Quantite: parseInt(this.state.Quantite, 10),
            categorie: this.state.categorie,
            product:this.state.product,
            orderDate:this.state.orderDate,
            sph : this.state.sph,
            comment : this.state.comment,
            
        });
    }


    render() {

        return (<div className="animated fadeIn">



            <div className="table">
                <div className="table-title">Create new Lenses Orders</div>
                <div className="table-content">
                    <div className="table-header">
                        <div className="table-row">
                            <div className="table-data">
                                <div>Category</div>

                            </div>
                            <div className="table-data">
                                <div style={{ display: 'flex' }}>Products</div>

                            </div>
                            <div className="table-data">
                                <div>Order date</div>
                            </div>
                            <div className="table-data">
                                <div>Quantity</div>
                            </div>
                            <div className="table-data">
                                <div>sph </div>
                            </div>
                            <div className="table-data">
                                <div>Comment</div>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="table-body">
                    {this.state.rowData.map((index) =>

                        <div className="table-row" key={index} id={index}   >



                            <div className="table-data">
                                <Select name="category" id="category"

                                    onChange={this.handleCategorieChange} >

                                    <MenuItem value={'Lenses'}>Lenses</MenuItem>

                                </Select>
                            </div>
                            <div className="table-data">

                                <Select
                                    labelId="product"
                                    id="product"

                                    onChange={this.handleProductChange}
                                >
                                    <MenuItem value={'Skysoft'}>Skysoft</MenuItem>

                                </Select>

                            </div>
                            <div className="table-data">
                                <MuiPickersUtilsProvider utils={DateFnsUtils}>
                                    <KeyboardDatePicker

