import React, { useState } from "react";
import TextareaAutosize from '@material-ui/core/TextareaAutosize';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import "./TableOrder.css";

const _defaultCosts = [
  
  {
    name: "lentille",
    price: 40
  }
];
const useStyles = makeStyles((theme) => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 200,
  },
}));
const CostTable = () => {
  const [costs, setCosts] = useState(_defaultCosts);

  const handleCostsChange = event => {
    const _tempCosts = [...costs];
    _tempCosts[event.target.dataset.id][event.target.name] = event.target.value;

    setCosts(_tempCosts);
    
  };

  const addNewCost = () => {
    setCosts(prevCosts => [...prevCosts, { name: "", price: 0 }]);
  };

  const getTotalCosts = () => {
    console.log(costs);
    return costs.reduce((total, item) => {
      return total + Number(item.price);
   
    }, 0);
  };
  const classes = useStyles();

  return (
    <div className="table">
      <div className="table-title">Create new order</div>
      <div className="table-content">
        <div className="table-header">
          <div className="table-row">
            <div className="table-data">
              <div>Category</div>
              
            </div>
            <div className="table-data">
              <div>Products</div>
              
            </div>
            <div className="table-data">
              <div>Price</div>
            </div>
            <div className="table-data">
              <div>Quantity</div>
            </div>
            <div className="table-data">
              <div>sph</div>
            </div>
            <div className="table-data">
              <div>Comment</div>
            </div>
          </div>
        </div>
        <div className="table-body">
          {costs.map((item, index) => (
            <div className="table-row" key={index}>
              <div className="table-data">
                
                <select
                 name="name"
                 data-id={index}
                 type="text"
                 value={item.name}
                 onChange={handleCostsChange}
                
                >
              <option value="produit">Produit</option>
              <option value="lentille">Lentille</option>
             
              </select>
              </div>
              <div className="table-data">
              <select
                 name="name"
                 data-id={index}
                 type="text"
                 //value={item.name}
                 onChange={handleCostsChange}
                
                >
              <option value="produit">Releasy</option>
              <option value="lentille">Skysoft</option>
             
              </select>
              </div>
              <div className="table-data">
              <form className={classes.container} noValidate>
      <TextField
        id="date"
        
        type="date"
        defaultValue="2017-05-24"
        className={classes.textField}
        InputLabelProps={{
          shrink: true,
        }}
      />
    </form>
              </div>
              <div className="table-data">
                <input
                  name="quantity"
                  data-id={index}
                  type="number"
                 //value={item.price}
                  onChange={handleCostsChange}
                />
              </div>
              <div className="table-data">
              <select
                 name="name"
                 data-id={index}
                 type="text"
                 value={item.sph}
                 onChange={handleCostsChange}
                
                >
              <option value="-1,00">-1,00</option>
              <option value="-1,25">-1,25</option>
              <option value="-1,25">-1,25</option>

              <option value="-1,5">-1,5</option>
              <option value="-1,75">-1,75</option>
              <option value="-2,00">-2,00</option>

              <option value="-2,25">-2,25</option>

              <option value="-2,50">-2,50</option>

              <option value="-2,75">-2,75</option>

              <option value="-3,00">-3,00</option>

              <option value="-3,25">-3,25</option>

              <option value="--3,50">-3,50</option>

              <option value="-3,75">-3,75</option>

              <option value="-4,00">-4,00</option>

              <option value="-4,25">-4,25</option>

              <option value="-4,50">-4,50</option>

              <option value="-4,75">-4,75</option>

              <option value="-5,00">-5,00</option>

              <option value="-5,25">-5,25</option>
              <option value="-5,50">-5,50</option>
              <option value="-5,75">-5,75</option>

              <option value="-6,00">-6,00</option>


             
              </select>
              </div>
              <div className="table-data">
              <TextareaAutosize
                  rowsMax={4}
                  name="comment"
                  data-id={index}
                  type="text"
                 
                  onChange={handleCostsChange}
                />
              </div>
            </div>
          ))}
          <div className="table-row">
            <div className="table-data">
              <button style={{backgroundColor: '#04E605', position: 'absolute', color: 'white' ,fontSize:'30 px'}} onClick={addNewCost}>  + Add   </button>
              
            </div>
          </div>
          
        </div>
        <div className="table-footer">
          <div className="table-row">
            <div className="table-data">
              <div>Total</div>
            </div>
            <div className="table-data">
              <div>{getTotalCosts()}</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CostTable;
