import { Component } from "react";
import React from 'react';
import clsx from 'clsx';
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/styles';
import { Card, CardContent, Grid, Typography, Avatar } from '@material-ui/core';
import AssignmentTurnedInIcon from '@material-ui/icons/AssignmentTurnedIn';
import axios from 'axios';
import * as OrderAPI from 'views/Orders/OrderManagement/api/OrderAPI';
class WV extends Component {

    state = {
        nb:0
    }
    componentDidMount() {
       this.nbwv();
    }
    nbwv = () => {
        const userId = JSON.parse(localStorage.getItem('user'));
         axios.get('http://localhost:4000/ordersLentille/countLensesOrdersByStatus/' +userId+'/'+"WaitingValidation", )
        .then(function (response) {
           
         if (response.data.success) {
           // this.setState({ nb: response.data.data[0].nb});
            console.log(this.state.nb);
            console.log(response.data.data[0].nb)
          }
           
      
        })
        .catch(function (error) {
      
          console.log(error);
        })
      
    };
    render() {
    return (
        <Card >
          <CardContent>
            <Grid
              container
              justify="space-between"
            >
              <Grid item>
                <Typography
                  style={ {fontWeight: 700}}
                  color="textSecondary"
                  gutterBottom
                  variant="body2"
                >Waiting Validation
                </Typography>
                
                <Typography variant="h6">{OrderAPI.getCountNborders("Produit")}</Typography>
                <Typography variant="h6">{this.state.nb}</Typography>
    
              </Grid>
              <Grid item>
                <Avatar style={ {backgroundColor:"#FBF269",height: 40, width: 40}}>
                  <AssignmentTurnedInIcon style={{  height: 32,width: 32}} />
                </Avatar>
              </Grid>
            </Grid>
    
    
          </CardContent>
        </Card>
      );
    };
    

}
export default WV;
